import {useEffect,useCallback} from 'react'
import { Layout,Icon,Input ,Avatar } from 'antd'
const { Header, Footer, Content } = Layout;
const { Search } = Input;
export default ({children})=>{
  return <Layout>
    <Header>
      <div className='logo'>
        <Icon type="github" style={{color:'white',fontSize:'35px'}} />  
      </div>
      <div className='header-searach'>
        <Search placeholder="请输入仓库名" />
      </div>
      <div className='avator'>
        <Avatar size="large" icon="user" />
      </div>
    </Header>
    <Content>{children}</Content>
    <Footer style={{ textAlign: 'center' }}>Next-Github Created by Marimo</Footer>
    <style jsx>{`
      .logo{
        line-height:64px;
      }
    `}</style>
    <style jsx global>
      {`#__next{
        height:100%;
      }
      .ant-layout{
        height:100%;
      }
      .ant-layout-header{
        display:flex;
        align-items:center;
        line-height:64px;
      }
      `}
    </style>
  </Layout>
}
