import {Button} from "antd"
import {useEffect} from 'react'
import axios from 'axios'
import Link from 'next/link'
//next自带方法
import getConfig from 'next/config'
import api from '../lib/api'
import { configConsumerProps } from "antd/lib/config-provider"
function Index({userRepos,userStarredRepos,isLogin}){
  // return <Layout>index</Layout>
  console.log(userRepos,
    userStarredRepos)
  return <span>index</span>
}

Index.getInitialProps=async ({ctx,reduxStore})=>{
  const user=reduxStore.getState().user
  if(!user||!user.id){
    return {
      isLogin:false,
      userRepos:{},
      userStaringRepos:{}
    }
  }
  const userRepos=await api.request({url:'/user/repos'},ctx.req,ctx.res)
  const userStaringRepos=await api.request({url:'/user/starred'},ctx.req,ctx.res)
   
  return {
    isLogin:true,
    userRepos:userRepos.data,
    userStarredRepos:userStarredRepos.data
  }
  
}

export default Index
